// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import "./ERC20Permit.sol";
import "./IBEP20.sol";

/**
 * @dev BEP20 Token Contract
 */

contract ERC20FanToken is ERC20Permit, IBEP20 {
    uint256 public constant MAX_CAP = 1000000000 * (10**18); // 1,000,000,000 tokens

    address public governance;

    event RecoverToken(
        address indexed token,
        address indexed destination,
        uint256 indexed amount
    );
    event GovernanceChanged(
        address indexed previousGovernance,
        address indexed newGovernance
    );

    modifier onlyGovernance() {
        require(msg.sender == governance, "!governance");
        _;
    }

    constructor() ERC20("FanToken", "FAW") {
        governance = msg.sender;
        _mint(governance, MAX_CAP);
    }

    function totalSupply()
        public
        view
        override(ERC20, IBEP20)
        returns (uint256)
    {
        return super.totalSupply();
    }

    function decimals() public view override(ERC20, IBEP20) returns (uint8) {
        return super.decimals();
    }

    function symbol()
        public
        view
        override(ERC20, IBEP20)
        returns (string memory)
    {
        return super.symbol();
    }

    function name()
        public
        view
        override(ERC20, IBEP20)
        returns (string memory)
    {
        return super.symbol();
    }

    function getOwner() external view override(IBEP20) returns (address) {
        return governance;
    }

    function balanceOf(address account)
        public
        view
        override(ERC20, IBEP20)
        returns (uint256)
    {
        return super.balanceOf(account);
    }

    function transfer(address recipient, uint256 amount)
        public
        override(ERC20, IBEP20)
        returns (bool)
    {
        return super.transfer(recipient, amount);
    }

    function allowance(address _owner, address spender)
        public
        view
        override(ERC20, IBEP20)
        returns (uint256)
    {
        return super.allowance(_owner, spender);
    }

    function approve(address spender, uint256 amount)
        public
        override(ERC20, IBEP20)
        returns (bool)
    {
        return super.approve(spender, amount);
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override(ERC20, IBEP20) returns (bool) {
        return super.transferFrom(sender, recipient, amount);
    }

    /**
     * @notice Function to set governance address
     * Owner is assumed to be governance
     * @param _governance Address of governance account
     */
    function setGovernance(address _governance) external onlyGovernance {
        require(_governance != address(0), "Invalid address");
        emit GovernanceChanged(msg.sender, _governance);
        governance = _governance;
    }

    /**
     * @notice Function to recover funds
     * Owner is assumed to be governance or Fantoken trusted party for helping users
     * @param token Address of token to be rescued
     * @param destination User address
     * @param amount Amount of tokens
     */
    function recoverToken(
        address token,
        address destination,
        uint256 amount
    ) external onlyGovernance {
        require(token != destination, "Invalid address");
        require(destination != address(0), "Invalid address");
        require(IERC20(token).transfer(destination, amount), "Retrieve failed");
        emit RecoverToken(token, destination, amount);
    }
}

